import './Counter.css' 
import React, {useState} from 'react';

function Counter(props) {

    const [count, setCount] = useState(0);
    function increment(step) {
        setCount(count + step);
    }
    return (
        <div className = "counter">
            <CounterButton by = {1} increment = {increment} />
            <CounterButton by = {5} increment = {increment} />
            <CounterButton by = {10} increment = {increment} />
            count
        </div>
    );
}

function CounterButton(props) {
    const [count, setCount] = useState(0);
    let by = props.by;
    return (
      <div className="Counter">
        <button onClick = {() => props.increment(by)}> +{by} </button>
        <span className = "count"> {count} </span>
      </div>
    );
  }

export default Counter;
