import React, {useState} from 'react';
import {BrowserRouter as Router, Route, useHistory, Switch, Link} from 'react-router-dom';

const UNINIT_LOGIN = 0;
const SUCCESS_LOGIN = 1;
const FAIL_LOGIN = 2;
function TodoApp() {
    return(
        <div>
            <Router>
                <Header/>
                <Switch>
                    <Route path = "/" exact component = {Login} />
                    <Route path = "/login" exact component = {Login} />
                    <Route path = "/welcome/:name" exact component = {Welcome} />
                    <Route path = "/todos" exact component = {ListTodos} />
                    <Route component = {Error} />
                </Switch>
                <Footer/>
            </Router>
        </div>
    );
}

function Header() {
    return(
        <header> 
            <nav className = "navbar navbar-expand-md navbar-dark bg-dark">
                <div><a className = "navbar-brand">Todo App</a></div>
                <ul className = "navbar-nav">
                    <li className = "nav-link">Home</li>
                    <li className = "nav-link">Todos</li>
                </ul>
                <ul className = "navbar-nav navbar-collapse justify-content-end">
                    <li className = "nav-link">Login</li>
                    <li className = "nav-link">Logout</li>
                </ul>
            </nav>
        </header>
    );
}

function Footer() {
    return(
        <div>
            <hr/> Footer
        </div>
    )
}

function Welcome(props) {
    return (
        <div> 
            Welcome {props.match.params.name}! You can manage your todos <Link to = '/todos'> here </Link>
        </div>
    );
}

function Login() {

    const [username, setUsername] = useState('username');
    const [password, setPassword] = useState('password');
    const [loginState, setLoginState] = useState(UNINIT_LOGIN);
    const history = useHistory();
    return(
        <div>
            <div>
                Username
                <input type = "text" name = "username" value = {username} onChange = {(event) => setUsername(event.target.value)} />
            </div>
            
            <div>
                Password
                <input type = "text" name = "password" value = {password} onChange = {(event) => setPassword(event.target.value)} />
            </div>
            <LoginButton username = {username} password = {password} setloginstate = {setLoginState} history = {history}/>
            {loginState === SUCCESS_LOGIN && <div> LOGIN SUCCESS</div>}
            {loginState === FAIL_LOGIN && <div> LOGIN FAIL</div>}
        </div>
    );
}

function LoginButton(props) {
    return (
        <div>
            <button onClick = {() => loginClick(props.username, props.password, props.setloginstate, props.history)}> Login </button>
        </div>
    );
}

function loginClick(username, password, setloginstate, history) {
    if (username === "josephxu1234" && password === "password") {
        console.log("Authentication success");
        setloginstate(SUCCESS_LOGIN);
        history.push(`/welcome/${username}`);
    }
    else {
        console.log("Failed");
        setloginstate(FAIL_LOGIN);
    }
}

function Error() {
    return (
        <div> 
            Error! Wrong path!
        </div>
    );
}

function ListTodos() {
    const [todos, setTodos] = useState(
        [
            {id: 1, description: 'learn react', done: false, targetDate: new Date()}, 
            {id: 2, description: 'fuck javascript', done: false, targetDate: new Date()}
        ]
        )
    return(
        <div>
        <h1>Todos</h1>
        <table>
            <thead>
            <tr>
                <th>id</th>
                <th>description</th>
                <th>is completed?</th>
                <th>target date</th>
            </tr>
            </thead>
            
            <tbody>
                {
                    todos.map(
                        todo =>
                        <tr>
                            <td>{todo.id}</td>
                            <td>{todo.description}</td>
                            <td>{todo.done.toString()}</td>
                            <td>{todo.targetDate.toString()}</td>
                        </tr>   
                    )
                }
            
            </tbody>
        </table>
        </div>
    );
}

export default TodoApp;
