import React, {useState} from 'react';
import {BrowserRouter as Router, Route, useHistory, Switch} from 'react-router-dom';

const UNINIT_LOGIN = 0;
const SUCCESS_LOGIN = 1;
const FAIL_LOGIN = 2;
function TodoApp() {
    return(
        <div>
            <Router>
                <Switch>
                    <Route path = "/" exact component = {Login} />
                    <Route path = "/login" exact component = {Login} />
                    <Route path = "/welcome/:name" exact component = {Welcome} />
                    <Route path = "/todos" exact component = {ListTodos} />
                    <Route component = {Error} />
                </Switch>
            </Router>
        </div>
    );
}

function Welcome(props) {
    return (
        <div> 
            Welcome {props.match.params.name}!
        </div>
    );
}

function Login() {

    const [username, setUsername] = useState('username');
    const [password, setPassword] = useState('password');
    const [loginState, setLoginState] = useState(UNINIT_LOGIN);
    const history = useHistory();
    return(
        <div>
            <div>
                Username
                <input type = "text" name = "username" value = {username} onChange = {(event) => setUsername(event.target.value)} />
            </div>
            
            <div>
                Password
                <input type = "text" name = "password" value = {password} onChange = {(event) => setPassword(event.target.value)} />
            </div>
            <LoginButton username = {username} password = {password} setloginstate = {setLoginState} history = {history}/>
            {loginState === SUCCESS_LOGIN && <div> LOGIN SUCCESS</div>}
            {loginState === FAIL_LOGIN && <div> LOGIN FAIL</div>}
        </div>
    );
}

function LoginButton(props) {
    return (
        <div>
            <button onClick = {() => loginClick(props.username, props.password, props.setloginstate, props.history)}> Login </button>
        </div>
    );
}

function loginClick(username, password, setloginstate, history) {
    if (username === "josephxu1234" && password === "password") {
        console.log("Authentication success");
        setloginstate(SUCCESS_LOGIN);
        history.push(`/welcome/${username}`);
    }
    else {
        console.log("Failed");
        setloginstate(FAIL_LOGIN);
    }
}

function Error() {
    return (
        <div> 
            Error! Wrong path!
        </div>
    );
}

function ListTodos() {
    const [todos, setTodos] = useState(
        [
            {id: 1, description: 'learn react'}, 
            {id: 2, description: 'fuck javascript'}
        ]
        )
    return(
        <div>
        <h1>todos</h1>
        <table>
            <thead>
            <tr>
                <th>id</th>
                <th>description</th>
            </tr>
            </thead>
            
            <tbody>
                {
                    todos.map(
                        todo =>
                        <tr>
                            <td>{todo.id}</td>
                            <td>{todo.description}</td>
                        </tr>   
                    )
                }
            
            </tbody>
        </table>
        </div>
    );
}

export default TodoApp;
