import React, { useState, useEffect, useRef} from 'react';
import TodoService from '../../api/todo/TodoService.js';
import AuthenticationService from './AuthenticationService.js'

function ListTodos() {
    const [todos, setTodos] = useState([]);
    const [numTodos, setNumTodos] = useState(0);

    useEffect(() => {
        let username = AuthenticationService.getUsername();
        TodoService.retrieveAllTodos(username).then( 
            response => {
                setTodos(response.data);
            }
        );
    }, [numTodos])
    return (
        <div>
            <h1>Todos</h1>

            <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Is completed?</th>
                            <th>Target Date</th>
                            <th>Update</th>
                            <th>Delete</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            todos.map(
                                todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.done.toString()}</td>
                                        <td>{todo.targetDate.toString()}</td>
                                        <td> <button className = "btn btn-success" onClick = {() => updateTodo(todo.id)}> Update </button> </td>
                                        <td> <button className = "btn btn-warning" onClick = {() => setNumTodos(todos - 1)}> Delete </button> </td>
                                    </tr>
                            )
                        }

                    </tbody>
                </table>
            </div>
        </div>
    );
}

function updateTodo(id) {
    console.log("update clicked" + id);
}

function deleteTodo(id) {
    let name = AuthenticationService.getUsername();
    TodoService.deleteTodo(name, id);

}
export default ListTodos;
