import React, { useState } from 'react';
import { BrowserRouter as Router, Route, useHistory, Switch, Link } from 'react-router-dom';
import AuthenticationService from './AuthenticationService.js'

const UNINIT_LOGIN = 0;
const SUCCESS_LOGIN = 1;
const FAIL_LOGIN = 2;
function TodoApp() {
    return (
        <div>
            <Router>
                <Header />
                <Switch>
                    <Route path="/" exact component={Login} />
                    <Route path="/login" exact component={Login} />
                    <Route path="/welcome/:name" exact component={Welcome} />
                    <Route path="/todos" exact component={ListTodos} />
                    <Route path="/logout" exact component={Logout} />
                    <Route component={Error} />
                </Switch>
                <Footer />
            </Router>
        </div>
    );
}

function Header() {
    const isUserLoggedIn = AuthenticationService.userLoggedIn();
    console.log(isUserLoggedIn);
    return (
        <header>
            <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                <div><a className="navbar-brand">josephxu1234</a></div>
                <ul className="navbar-nav">
                    {isUserLoggedIn && <li key = "welcome"> <Link to="/welcome/josephxu1234" className="nav-link">Home</Link></li>}
                    {isUserLoggedIn && <li key = "todos"> <Link to="/todos" className="nav-link">Todos</Link></li>}
                </ul>
                <ul className="navbar-nav navbar-collapse justify-content-end">
                    {!isUserLoggedIn && <li key = "login"> <Link to="/login" className="nav-link">Login </Link></li>}
                    {isUserLoggedIn && <li key = "logout"> <Link to="/logout" className="nav-link" onClick={AuthenticationService.logout}>Logout </Link></li>}
                </ul>
            </nav>
        </header>
    );
}

function Footer() {
    return (
        <footer className="footer">
            <span className="text-muted"> All Rights Reserved</span>
        </footer>
    )
}

function Logout() {
    return (
        <div>
            <h1> You are logged out </h1>
            <div className="container">
                Thank you for using this application
            </div>
        </div>
    );
}

function Welcome(props) {
    return (
        <div>
            <h1> Welcome! </h1>
            <div className="container">
                Welcome {props.match.params.name}! You can manage your todos <Link to='/todos'> here </Link>
            </div>
        </div>
    );
}

function Login() {

    const [username, setUsername] = useState('username');
    const [password, setPassword] = useState('password');
    const [loginState, setLoginState] = useState(UNINIT_LOGIN);
    const history = useHistory();
    return (
        <div>
            <div>
                Username
                <input type="text" name="username" value={username} onChange={(event) => setUsername(event.target.value)} />
            </div>

            <div>
                Password
                <input type="text" name="password" value={password} onChange={(event) => setPassword(event.target.value)} />
            </div>
            <LoginButton className="btn btn-success" username={username} password={password} setloginstate={setLoginState} history={history} />
            {loginState === SUCCESS_LOGIN && <div> LOGIN SUCCESS</div>}
            {loginState === FAIL_LOGIN && <div className="alert alert-warning"> LOGIN FAIL</div>}
        </div>
    );
}

function LoginButton(props) {
    return (
        <div>
            <button onClick={() => loginClick(props.username, props.password, props.setloginstate, props.history)}> Login </button>
        </div>
    );
}

function loginClick(username, password, setloginstate, history) {
    if (username === "josephxu1234" && password === "password") {
        AuthenticationService.registerSuccessfulLogin(username, password);
        console.log("Authentication success");
        setloginstate(SUCCESS_LOGIN);
        history.push(`/welcome/${username}`);
    }
    else {
        console.log("Failed");
        setloginstate(FAIL_LOGIN);
    }
}

function Error() {
    return (
        <div>
            Error! Wrong path!
        </div>
    );
}

function ListTodos() {
    const [todos, setTodos] = useState(
        [
            { id: 1, description: 'learn react', done: false, targetDate: new Date() },
            { id: 2, description: 'fuck javascript', done: false, targetDate: new Date() }
        ]
    )
    return (
        <div>
            <h1>Todos</h1>

            <div className="container">
                <table className = "table">
                    <thead>
                        <tr>
                            <th key = "description">description</th>
                            <th key = "iscompleted">is completed?</th>
                            <th key = "target">target date</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            todos.map(
                                todo =>
                                    <tr>
                                        <td key = {todo.description}>{todo.description}</td>
                                        <td key = {todo.done.toString()}>{todo.done.toString()}</td>
                                        <td key = {todo.targetDate.toString()}>{todo.targetDate.toString()}</td>
                                    </tr>
                            )
                        }

                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default TodoApp;
